import * as employeeActions from './employee.actions';

export interface IEmployee {
    sno: string;
    name: string;
    age: number;
    designation: string;
    location: string;
}

export interface EmployeeState {
    employees : IEmployee[]
}

let initialState :EmployeeState = {
    employees:[] as IEmployee[]
};

export const reducer =(state=initialState,action:employeeActions.ActionType) =>{
switch (action.type) {
    case employeeActions.FETCH_EMPLOYEE_DATA:
        return{
            employees:[
                {
                    sno : 'AA00101',
                    name : 'Rajan',
                    age : 25,
                    designation : 'Software Engineer',
                    location : 'Bangalore'
                },
                {
                    sno : 'AA00102',
                    name : 'Mahesh',
                    age : 28,
                    designation : 'Sr.Software Engineer',
                    location : 'Bangalore'
                },
                {
                    sno : 'AA00103',
                    name : 'John',
                    age : 45,
                    designation : 'Project Manager',
                    location : 'Bangalore'
                },
                {
                    sno : 'AA00104',
                    name : 'Wilson',
                    age : 48,
                    designation : 'Director',
                    location : 'Bangalore'
                }
            ]

        };
    default:return state;

}
};
