import React from 'react';

interface Iproduct{
    sno:string;
    name:string;
    image:string;
    price:number;
    qty:number;
}

interface IProps {}

interface IState {
   product:Iproduct
}

class ProductItems extends React.Component<IProps, IState>{

    constructor(props:IProps) {
        super(props);
         this.state={
            product:{
                sno:'AA101',
                name:'Mi Watch',
                image:'',
                price:1500,
                qty:2,
            }
        }
    }
      incrQty= () =>{

      };
    render() {
        let{product} = this.state;
        return (
            <React.Fragment>
              <section className="mt-3">
                  <div className="container">
                      <div className="row">
                          <div className="col">
                              <p className="h3 text-primary">Product Item</p>
                              <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Autem, fuga harum in magni nulla quam. Aliquid, asperiores, beatae culpa delectus deleniti explicabo itaque minus nesciunt porro quasi quis quod soluta?</p>
                          </div>
                      </div>
                  </div>
              </section>
                <section className="mt-3">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <p className="h3 text-primary">Product Item</p>
                               <table className="table table-hover text-center table-striped ">
                                   <thead className="bg-dark text-primary">
                                   <tr>
                                       <th>SNo</th>
                                       <th>Image</th>
                                       <th>Name</th>
                                       <th>Price</th>
                                       <th>Qty</th>
                                       <th>Total</th>
                                   </tr>
                                   </thead>
                                <tbody>
                                <tr>
                                    <td>{product.sno}</td>
                                    <td>{product.image}</td>
                                    <td>{product.name}</td>
                                    <td>{product.price}</td>
                                    <i className="fa-minus-circle mx-1" onClick={this.incrQty}/>
                                    <td>{product.qty}</td>
                                    <td>{product.price*product.qty}</td>
                                </tr>
                                </tbody>
                               </table>
                            </div>
                        </div>
                    </div>
                </section>
            </React.Fragment>
        );
    }
}
export default ProductItems;
