import * as pizzaActions from './pizzaHut.actions';


//State Type
export interface pizzaState {
    count:number;
}

//initial State

let initialState:pizzaState ={
    count:55
};


//Reducer
 export const reducer = (state=initialState,action:pizzaActions.ActionType)=>{
     switch (action.type) {
         case pizzaActions.BUY_PIZZA:
             return{
               count:(state.count -1 > 0) ? state.count - 1:0
             };
         default:return state;
     }
 };
