import React, {useEffect, useState} from "react";
import {useParams} from 'react-router-dom';
import axios from 'axios';
import {IEmployee} from "./models/IEmployee";

interface URLParamTypes{
    employeeId:string;
}

interface IProps {}
interface IState {
    selectedEmployee : IEmployee
}

let EmployeeDetails:React.FC<IProps> =() =>{
    let{employeeId} = useParams<URLParamTypes>();
    let[employeeState,setEmployeeState] = useState<IState>({
        selectedEmployee : {} as IEmployee
    });
    useEffect(()=>{
        let dataURL : string = 'https://gist.githubusercontent.com/thenaveensaggam/2c0a106c0b66ef2c8f4eb2b2c8c31e6d/raw/416efcc2300afc13cd0aa6413b2de4918a50ad30/contacts-02-12-2020.json';
        axios.get(dataURL).then((response) => {
            let employees:IEmployee[] = response.data;
            let singleEmployee:IEmployee | undefined = employees.find(employee => {
                return employee.login.uuid ===employeeId;
            });
            if(singleEmployee !==undefined){
                setEmployeeState({
                    selectedEmployee: singleEmployee
                });
            }
        }).catch((error) => {
        });
    },[]);

    return(
        <React.Fragment>
            <h2> EmployeeDetails Components</h2>

        </React.Fragment>
    )
};
export default EmployeeDetails;
