import React, {useState} from 'react';
import './App.css';
import {UserInfo} from "./components/Props-Drilling/UserInfo";
import ComponentA from "./components/Props-Drilling/ComponentA";
import UserContext from './components/Context-Api/UserContext';
import ComponentOne from "./components/Context-Api/ComponentOne";

interface IProps {}
interface IState {
    userInfo: UserInfo
}

let App:React.FC<IProps> =() =>{
    let [appState,setAppState]= useState<IState>({
        userInfo : {
            author : 'NAVEEN SAGGAM',
            channel : 'UiBrains Technologies'
        }
    });

    return(
        <React.Fragment>
       <nav className="navbar navbar-dark bg-dark navbar-expand-sm">
           <a href="/" className="navbar-brand"> React Hooks - Props Drilling -Context API </a>
       </nav>
            {/*props Drilling Concept*/}
            {/*<section className="mt-3">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <div className="card">
                                    <div className="card-body bg-success text-white">
                                        <p className="h4">App Component</p>
                                        <small>{JSON.stringify(appState.userInfo)}</small>
                                        <ComponentA userInfo={appState.userInfo}/>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>*/}

            {/* Context API */}

            <section className="mt-3">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <div className="card">
                                    <div className="card-body bg-success text-white">
                                        <p className="h4">App Component</p>
                                        <small>{JSON.stringify(appState.userInfo)}</small>

                                        <UserContext.Provider value={appState.userInfo}>
                                            <ComponentOne/>
                                        </UserContext.Provider>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>

        </React.Fragment>
    )
};
export default App;
