import React from 'react';

interface IProduct{
    sno:string;
    name:string;
    image:string;
    price:number;
    qty:number;
}
interface IProps {}

interface IState {
    products:IProduct[]
}

class ShoppingCart extends React.Component<IProps, IState>{

    constructor(props:IProps) {
        super(props);
        this.state={
            products:[
                {
                    sno:'AA101',
                    name:'Mi Watch',
                    image:'',
                    price:1500,
                    qty:2,
                },
                {
                    sno:'AA102',
                    name:'Apple Watch',
                    image:'',
                    price:1800,
                    qty:2,
                },
                {
                    sno:'AA103',
                    name:'Samsung Watch',
                    image:'',
                    price:2000,
                    qty:2,
                },
            ]
        };
    }
    incrQty =(sno:string) => {
        let updatedproducts :IProduct[] =this.state.products.map(product =>{
            if(product.sno === sno){
                return{
                    ...product,
                    qty : product.qty+1
                }
            }
            return product;
        });
        this.setState({
            products :[...updatedproducts]
        });
    };

    decrQty =(sno:string) => {
        let updatedproducts :IProduct[] =this.state.products.map(product =>{
            if(product.sno === sno){
                return{
                    ...product,
                    qty : product.qty-1 > 0 ? product.qty -1:1
                }
            }
            return product;
        });
        this.setState({
            products :[...updatedproducts]
        });
    };

    render() {
        let {products} = this.state;
        return (
            <React.Fragment>
                <section className="mt-3">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <p className="h3 text-primary">Product Item</p>
                                <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Autem, fuga harum in magni nulla quam. Aliquid, asperiores, beatae culpa delectus deleniti explicabo itaque minus nesciunt porro quasi quis quod soluta?</p>
                            </div>
                        </div>
                    </div>
                </section>
                <section className="mt-3">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <table className="table table-hover text-center table-striped ">
                                    <thead className="bg-dark text-primary">
                                    <tr>
                                        <th>SNo</th>
                                        <th>Image</th>
                                        <th>Name</th>
                                        <th>Price</th>
                                        <th>Qty</th>
                                        <th>Total</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    {
                                        products.map(product => {
                                            return(
                                                <tr>
                                                   <td>{product.sno}</td>
                                            <td>{product.image}</td>
                                            <td>{product.name}</td>
                                            <td>&#8377;{product.price.toFixed(2)}</td>
                                            <td>
                                                <i className=" fa fa-minus-circle mx-1" />
                                                {product.qty}
                                                <i className=" fa fa-plus-circle mx-1" onClick={this.incrQty.bind(this,product.sno)} />
                                            </td>
                                            <td>&#8377;{(product.price*product.qty).toFixed(2)}</td>
                                                </tr>
                                            )
                                    })
                                    }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </section>
            </React.Fragment>
        );
    }
}
export default ShoppingCart;
