import React, {useEffect, useState} from "react";
import {IUser} from "./IUser";
import * as userActions from '../redux/user/user.actions';
import * as userReducer from '../redux/user/user.reducer';
import {useDispatch,useSelector} from "react-redux";

interface IProps {}
interface IState {
    users : IUser[]
}

let UserList:React.FC<IProps> =() =>{
    let dispatch=useDispatch();

    let[userState,setUserState] = useState<IState>({
        users : [] as IUser[]
    });

    useEffect(() =>{
        dispatch(userActions.fetchUser());
    },[]);
    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <table className="table table-hover text-center table-primary table-striped">
                                <thead>
                                <tr>
                                    <th>SNO</th>
                                    <th>NAME</th>
                                    <th>Email</th>
                                    <th>Phone</th>
                                    <th>Website</th>
                                    <th>Location</th>
                                </tr>
                                </thead>
                                <tbody>
                                {
                                    userState.users.length > 0 &&
                                    userState.users.map(user => {
                                        return (
                                            <tr key={user.id}>
                                                <td>{user.id}</td>
                                                <td>{user.name}</td>
                                                <td>{user.email}</td>
                                                <td>{user.phone}</td>
                                                <td>{user.website}</td>
                                                <td>{user.address.city}</td>
                                            </tr>
                                        )
                                    })
                                }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default UserList;
