import React, {useState} from "react";
import {useDispatch,useSelector} from "react-redux";
import * as messageActions from '../redux/Wish-Message/wish-message.actions';
import  * as messageReducer from '../redux/Wish-Message/wish-message.reducer';


interface IProps {}


let WishMessage:React.FC<IProps> =() =>{
    let dispatch = useDispatch();
    let messageState:messageReducer.MessageState=useSelector((state:{msg:messageReducer.MessageState}) =>{
        return state.msg;
    });
  let clickMorning = () =>{
      dispatch(messageActions.sayGoodMorning());
  };

    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-8">
                            <div className="card">
                                <div className="card-body">
                                    <h3>{messageState.message}</h3>
                                    <button onClick={clickMorning}  className="btn btn-success btn-sm">Good Morning</button>
                                    <button  className="btn btn-warning btn-sm">Good Afternoon</button>
                                    <button  className="btn btn-danger btn-sm">Good Evening</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default WishMessage;
