import React, {useReducer, useState} from "react";
interface IProps {}
interface IState {
    message:string;
}
let initialState:IState ={
    message:'Hello'
}

interface ActionType{
    type:string;
}



let reducer =(state:IState=initialState,action:ActionType)=>{
  switch (action.type) {
      case 'gm':
          return{message:'Good Morning'};
      case 'ga':
          return{message:'Good Afternoon'};
      case 'ge':
          return{message:'Good Evening'};
      default:return state;

  }
};


let MessageTwo:React.FC<IProps> =() =>{
    let [messageState,dispatch]= useReducer(reducer,initialState);

    let sayGoodMorning =() =>{
      dispatch({type:'gm'});
    };
    let sayGoodAfternoon =() =>{
        dispatch({type:'ga'});
    };
    let sayGoodEvening =() =>{
        dispatch({type:'ge'});
    };


    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-8">
                            <div className="card">
                                <div className="card-header bg-warning text-white" >
                                    <p className="h4">using useReducer()</p>
                                </div>
                                <div className="card-body">
                                    <p className="h3">{messageState.message}</p>
                                    <button onClick={sayGoodMorning}  className="btn btn-success btn-sm">Good Morning</button>
                                    <button onClick={sayGoodAfternoon} className="btn btn-warning btn-sm">Good Afternoon</button>
                                    <button onClick={sayGoodEvening} className="btn btn-danger btn-sm">Good Evening</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

            </section>
        </React.Fragment>
    )
};
export default MessageTwo;
