import React from 'react';
import './Message.css';

interface IProps {
msg:string;
}

interface IState {}

class Message extends React.Component<IProps, IState>{

    constructor(props:IProps) {
        super(props);
    }

    render() {
        return (
            <React.Fragment>
                <div className="card">
                    <div className="card-body">
                        <h1>{this.props.msg}</h1>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ad corporis dolore eveniet explicabo facere ipsum quod, voluptas! Ipsa ut, vero.</p>

                    </div>
                </div>
            </React.Fragment>
        );
    }
}
export default Message;
