import React from 'react';
import './App.css';
import {BrowserRouter as Router , Route , Switch} from 'react-router-dom';
import NavBar from "./components/layout/NavBar";
import Home from "./components/layout/Home";
import ProductList from "./components/products/ProductList";
import ProductAdmin from "./components/products/ProductAdmin";
import CreateProduct from "./components/products/CreateProduct";
import UpdateProduct from "./components/products/UpdateProduct";

interface IProps {}
interface IState {

}

class App extends React.Component<IProps, IState>{
    constructor(props:IProps) {
        super(props);

    }

    render() {
        return (
            <React.Fragment>
                <Router>
                    <NavBar/>
                    <Switch>
                        <Route exact path="/" component={Home}/>
                        <Route exact path="/products/list" component={ProductList}/>
                        <Route exact path="/products/admin" component={ProductAdmin}/>
                        <Route exact path="/products/create" component={CreateProduct}/>
                        <Route exact path="/products/:productId" component={UpdateProduct}/>
                    </Switch>
                </Router>
            </React.Fragment>
        );
    }
}
export default App;
