import React from "react";
import * as authActions from '../redux/auth-user/auth-user.actions';
import * as authReducer from '../redux/auth-user/auth-user.reducer';
import {useDispatch,useSelector} from 'react-redux';

interface IProps {}


let AuthUser:React.FC<IProps> =() =>{
    let dispatch=useDispatch();
    let authState:authReducer.AuthState=useSelector((state:{auth:authReducer.AuthState}) =>{
        return state.auth;
    });

    let login =() =>{
        dispatch(authActions.loginUser());
    };
    let logout =() =>{
       dispatch(authActions.logoutUser());
    };

    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-7">
                            <div className="card">
                                <div className="card-body bg-light">
                                    {
                                        !authState.isLoggedIn ?
                                            <button className="btn btn-success btn-sm" onClick={login}>Login</button> :
                                            <button className="btn btn-danger btn-sm" onClick={logout}>LogOut</button>
                                    }
                                    {
                                        authState.isLoggedIn ?
                                            <h1>Welcome User</h1> : <h1>Welcome Guest</h1>
                                    }
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default AuthUser;
