import React, {useState} from "react";
interface IProps {}
interface IState {
    message:string;
}

let MessageTwo:React.FC<IProps> =() =>{
    let[messageState,setMessageState]= useState<IState>({
        message :'Hello'
    });


    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-8">
                            <div className="card">
                                <div className="card-header bg-warning text-white" >
                                    <p className="h4">using useReducer()</p>
                                </div>
                                <div className="card-body">
                                    <p className="h3">{messageState.message}</p>
                                    <button  className="btn btn-success btn-sm">Good Morning</button>
                                    <button  className="btn btn-warning btn-sm">Good Afternoon</button>
                                    <button  className="btn btn-danger btn-sm">Good Evening</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

            </section>
        </React.Fragment>
    )
};
export default MessageTwo;
