import React, {useState} from "react";
import {useDispatch,useSelector} from 'react-redux';
import * as cartActions from '../redux/shopping-cart/shopping-cart.actions';
import * as cartReducer from '../redux/shopping-cart/shopping-cart.reducer';
interface IProps {}


let ShoppingCart:React.FC<IProps> =() =>{
    let dispatch = useDispatch();

    // read the state data from Redux store
    let cartState:cartReducer.CartState = useSelector((state:{cart:cartReducer.CartState}) => {
        return state.cart;
    });

    let decrQty =(sno:string) =>{
        dispatch(cartActions.decrProductQty(sno));
    };
    let incrQty =(sno:string) =>{
        dispatch(cartActions.incrProductQty(sno));
    };



let {products} =cartState;

    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-primary">Shopping Cart</p>
                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Error et exercitationem fuga, nobis qui quo totam voluptas voluptatem? Ad architecto earum id natus officia quia reprehenderit rerum soluta vero voluptates.</p>
                        </div>
                    </div>
                </div>
            </section>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <table className="table table-hover text-center table-striped">
                                <thead className="bg-dark text-primary">
                                <tr>
                                    <th>SNO</th>
                                    <th>Image</th>
                                    <th>Name</th>
                                    <th>Price</th>
                                    <th>Qty</th>
                                    <th>Total</th>
                                </tr>
                                </thead>
                                <tbody>
                                {
                                    products.map(product => {
                                        return (
                                            <tr key={product.sno}>
                                                <td>{product.sno}</td>
                                                <td>
                                                    <img src={product.image} alt="" width="50" height="50"/>
                                                </td>
                                                <td>{product.name}</td>
                                                <td>&#8377; {product.price.toFixed(2)}</td>
                                                <td>
                                                    <i className="fa fa-minus-circle mx-1" onClick={decrQty.bind(this, product.sno)}/>
                                                    {product.qty}
                                                    <i className="fa fa-plus-circle mx-1" onClick={incrQty.bind(this, product.sno)}/>
                                                </td>
                                                <td>&#8377; {(product.price * product.qty).toFixed(2)}</td>
                                            </tr>
                                        )
                                    })
                                }
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default  ShoppingCart;
