import React from 'react';

interface IProps {}

interface IState {
    msg:string;
}

class WishMessage extends React.Component<IProps, IState>{

    constructor(props:IProps) {
        super(props);
        this.state={
            msg:'Hello'
        }
    }
sayGoodMorning= () => {
    this.setState({
        msg:'Good Morning'
    });
};
    sayGoodAfternoon=(value:string) =>{
        this.setState({
            msg:value
        });
    };
    render() {
        return (
            <React.Fragment>
                <section className="mt-3">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-8">
                                <div className="card">
                                    <div className="card-body">
                                        <h3>{this.state.msg}</h3>
                                        <button onClick={this.sayGoodMorning} className="btn btn-success btn-sm">Good Morning</button>
                                        <button onClick={this.sayGoodAfternoon.bind(this,'Good Afternoon')} className="btn btn-warning btn-sm">Good Afternoon</button>
                                        <button onClick={() => this.setState({msg:'Good Evening'})} className="btn btn-danger btn-sm">Good Evening</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </React.Fragment>
        );
    }
}
export default WishMessage;
