import React, {useEffect} from "react";
import * as employeeActions from '../redux/employee/employee.actions';
import * as employeeReducer from '../redux/employee/employee.reducer';
import {useDispatch,useSelector} from "react-redux";


interface IProps {}


let EmployeeCards:React.FC<IProps> =() =>{
    let dispatch= useDispatch();

    let employeeState:employeeReducer.EmployeeState= useSelector((state:{employee:employeeReducer.EmployeeState}) =>{
       return state.employee;
    });

    useEffect(() =>{
        dispatch(employeeActions.fetchEmployeeData());
    },[]);

    let{employees} = employeeState;

    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3">Employee Details</p>
                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Alias aliquam, beatae cumque delectus dolores eveniet itaque maxime molestiae nemo nulla pariatur perferendis ratione rem repellat similique, sint sit temporibus veritatis.</p>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            {
                                employees.map(employee => {
                                    return (
                                        <div className="card my-2" key={employee.sno}>
                                            <div className="card-body rgba-blue-grey-light">
                                                <ul className="list-group">
                                                    <li className="list-group-item">{employee.sno}</li>
                                                    <li className="list-group-item">{employee.name}</li>
                                                    <li className="list-group-item">{employee.age}</li>
                                                    <li className="list-group-item">{employee.designation}</li>
                                                    <li className="list-group-item">{employee.location}</li>
                                                </ul>
                                            </div>
                                        </div>
                                    )
                                })
                            }
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default EmployeeCards;
