import React, {useState} from "react";
interface IProps {}
interface IState {
    pText : string;
    cText : string;
}

let ParentCard:React.FC<IProps> =() =>{
    let [parentState,setParentState] =useState<IState>({
        pText : '',
        cText : ''
    });
   let updateInput = (event : React.ChangeEvent<HTMLInputElement>) => {
        setParentState({
            ...parentState,
            pText : event.target.value
        });
    };
   let receiveData = (value : string):void => {
        setParentState({
            ...parentState,
            cText : value
        });
    };


    return(
        <React.Fragment>
            <h2> ParentCard Components</h2>

        </React.Fragment>
    )
};
export default ParentCard;
