import React, {useState} from "react";
import {
    Form,
    Dropdown,
    DropdownButton,
    Container,
} from "react-bootstrap";




interface IProps {}
interface IState {

}

let DropDown:React.FC<IProps> =() =>{

        let [isShowSensor, setISShowSensor] = useState(false);
        let [isShowMoter, setISShowMoter] = useState(false);
       let [isShowRelay, setISShowRelay] = useState(false);

    return(
        <React.Fragment>

            <div>
                <Container>
                    <h1 className="DropDown">Hi This IS Services Page</h1>
                    <DropdownButton
                        id="dropdown-basic-button"
                        title="Choose Device"
                        style={{ alignContent: "center", justifyContent: "center" }}
                    >
                        <Dropdown.Item onClick={() => setISShowSensor(!isShowSensor)}>
                            Sensor
                        </Dropdown.Item>
                        <Dropdown.Item onClick={() => setISShowMoter(!isShowMoter)}>
                            Moter
                        </Dropdown.Item>
                        <Dropdown.Item onClick={() => setISShowRelay(!isShowRelay)}>
                            Relay
                        </Dropdown.Item>
                    </DropdownButton>

                    {isShowSensor && (
                        <Form>
                            <Form.Control
                                type="text"
                                placeholder="Sensor Id"
                                style={{ marginTop: "10px" }}
                            />

                            <Form.Control
                                type="text"
                                placeholder="Sensor Name"
                                style={{ marginTop: "10px" }}
                            />
                        </Form>
                    )}
                    {isShowMoter && (
                        <Form>
                            <Form.Control
                                type="text"
                                placeholder="Moter Id"
                                style={{ marginTop: "10px" }}
                            />

                            <Form.Control
                                type="text"
                                placeholder="Moter Name"
                                style={{ marginTop: "10px" }}
                            />
                        </Form>
                    )}
                    {isShowRelay && (
                        <Form>
                            <Form.Control
                                type="text"
                                placeholder="Relay Id"
                                style={{ marginTop: "10px" }}
                            />

                            <Form.Control
                                type="text"
                                placeholder="Relay Name"
                                style={{ marginTop: "10px" }}
                            />
                            <Form.Control
                                type="text"
                                placeholder="Modules"
                                style={{ marginTop: "10px" }}
                            />
                        </Form>
                    )}
                </Container>
            </div>

        </React.Fragment>
    )
};
export default DropDown;
