import React, {useState} from "react";
interface IProps {
    pText : string;
    sendData: (value: string) => void
}
interface IState {
    cText: string;
}

let ChildCard:React.FC<IProps> =() =>{
    let [childState,setChildState] = useState<IState>({
        cText : ''
    });
   let updateInput = (event : React.ChangeEvent<HTMLInputElement>) => {
        setChildState({
            cText : event.target.value
        });
        this.props.sendData(event.target.value);
    };



    return(
        <React.Fragment>
            <h2> ChildCard Components</h2>

        </React.Fragment>
    )
};
export default ChildCard;
