import React from "react";
import {useSelector,useDispatch} from 'react-redux';
import * as pizzaActions from '../redux/pizzahut/pizzaHut.actions';
import * as pizzaReducer from '../redux/pizzahut/pizzaHut.reducer';
import {Simulate} from "react-dom/test-utils";


interface IProps {}
interface IState {

}

let PizzaHut:React.FC<IProps> =() =>{
    let dispatch = useDispatch();

    //Read the Redux store data
let pizzaState:pizzaReducer.pizzaState=useSelector((state:{pizza:pizzaReducer.pizzaState}) =>{
    return state.pizza;
});

// dispatch an action to Redux store for click event
let clickBuyPizza =() =>{
    dispatch(pizzaActions.buyPizza());
};
    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3">PizzaHut</p>
                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ab aperiam beatae ducimus et harum impedit iste placeat quia quisquam vitae? Ab aut blanditiis dignissimos doloribus incidunt magnam numquam rerum velit?</p>
                        </div>
                    </div>
                </div>
                <div className="container">
                    <div className="row">
                        <div className="col-md-6">
                            <img src="https://b.zmtcdn.com/data/pictures/4/18833804/7398da37ab73c4c7cb16ac860e577369.jpg?fit=around|771.75:416.25&crop=771.75:416.25;*,*" alt="" className="img-fluid"/>
                        </div>
                        <div className="col-md-6">
                            <p className="h2">Veg Pizza</p>
                            <p className="lead">Available Pizza in Store</p>
                            <p className="h2">{pizzaState.count}</p>
                            <button onClick={clickBuyPizza} className="btn btn-success btn-sm">Buy Pizza</button>
                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aliquid amet debitis, dolor dolorem eaque iusto nulla odit optio. Commodi debitis fuga odit sint. Accusantium corporis error iusto nemo nisi quos.</p>
                        </div>
                    </div>
                </div>
            </section>

        </React.Fragment>
    )
};
export default PizzaHut;
