import React, {useState} from "react";
interface IProps {}
interface IState {
    message:string;
}

let MessageOne:React.FC<IProps> =() =>{
    let[messageState,setMessageState]= useState<IState>({
        message :'Hello'
    });
    let sayGoodMorning =() =>{

        setMessageState({
            message:'Good Morning'
        })
    };
    let sayGoodAfternoon =() =>{

        setMessageState({
            message:'Good Afternoon'
        })
    };
    let sayGoodEvening =() =>{

        setMessageState({
            message:'Good Evening'
        })
    };

    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-8">
                            <div className="card">
                                <div className="card-header bg-info text-white" >
                                    <p className="h4">using useState()</p>
                                </div>
                                <div className="card-body">
                                    <p className="h3">{messageState.message}</p>
                                    <button onClick={sayGoodMorning} className="btn btn-success btn-sm">Good Morning</button>
                                    <button onClick={sayGoodAfternoon} className="btn btn-warning btn-sm">Good Afternoon</button>
                                    <button onClick={sayGoodEvening} className="btn btn-danger btn-sm">Good Evening</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

            </section>
        </React.Fragment>
    )
};
export default MessageOne;
