import React, {useState} from "react";
import ChildComponent from "./ChildComponent";
interface IProps {}
interface IState {
    pMessage : string;
    childMessage : string;
}

let ParentComponent:React.FC<IProps> =() =>{
    let[parentState,serParentState]=useState<IState>({
        pMessage : 'Hello Iam from Parent',
        childMessage : ''
    });
     let receiveData = (value:string):void => {
        serParentState({
            ...parentState,
            childMessage : value
        });
    };

    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-8">
                            <div className="card">
                                <div className="card-body bg-success text-white">
                                    <p className="h3">Parent Component</p>
                                    <small>{parentState.pMessage}</small><br/>
                                    <small>{parentState.childMessage}</small>
                                    <ChildComponent message={parentState.pMessage} sendData={receiveData}/>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default ParentComponent;
