import React from 'react';

interface IProduct{
    sno:string;
    name:string;
    image:string;
    price:number;
    qty:number;
}
interface IProps {}

interface IState {
    products:IProduct[]
}

class ShoppingCart extends React.Component<IProps, IState>{

    constructor(props:IProps) {
        super(props);
        this.state={
            products:[
                {
                    sno:'AA101',
                    name:'Mi Watch',
                    image:'https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.reliancedigital.in%2Foppo-ow19w8-smart-watch-46mm-black%2Fp%2F491900835&psig=AOvVaw1AbMon1HUU204kGyZCrTea&ust=1612345749068000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCOjOkY_2yu4CFQAAAAAdAAAAABAQ',
                    price:1500,
                    qty:2,
                },
                {
                    sno:'AA102',
                    name:'Apple Watch',
                    image:'https://images-na.ssl-images-amazon.com/images/I/81y4iaxFkkL._AC_SY355_.jpg',
                    price:1800,
                    qty:2,
                },
                {
                    sno:'AA103',
                    name:'Samsung Watch',
                    image:'https://images-na.ssl-images-amazon.com/images/I/71Yk-Mhv9DL._SL1500_.jpg',
                    price:2000,
                    qty:2,
                },
            ]
        }
    }


    render() {
        let {products} = this.state;
        return (
            <React.Fragment>
                <section className="mt-3">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <p className="h3 text-primary">Product Item</p>
                                <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Autem, fuga harum in magni nulla quam. Aliquid, asperiores, beatae culpa delectus deleniti explicabo itaque minus nesciunt porro quasi quis quod soluta?</p>
                            </div>
                        </div>
                    </div>
                </section>
                <section className="mt-3">
                    <div className="container">
                        <div className="row">
                            <div className="col">
                                <table className="table table-hover text-center table-striped ">
                                    <thead className="bg-dark text-primary">
                                    <tr>
                                        <th>SNo</th>
                                        <th>Image</th>
                                        <th>Name</th>
                                        <th>Price</th>
                                        <th>Qty</th>
                                        <th>Total</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    {
                                        products.map(product => {
                                            return(
                                                <tr>
                                                   <td>{product.sno}</td>
                                            <td>{product.image}</td>
                                            <td>{product.name}</td>
                                            <td>&#8377;{product.price.toFixed(2)}</td>
                                            <td>
                                                <i className=" fa fa-minus-circle mx-1" />
                                                {product.qty}
                                                <i className=" fa fa-plus-circle mx-1" />
                                            </td>
                                            <td>&#8377;{(product.price*product.qty).toFixed(2)}</td>
                                                </tr>
                                            )
                                    })
                                    }
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </section>
            </React.Fragment>
        );
    }
}
export default ShoppingCart;
