import axois from 'axios';
import {IContact} from "../../components/models/IContact";

export const FETCH_CONTACT_REQUEST:string='FETCH_CONTACT_REQUEST';
export const FETCH_CONTACT_SUCCESS:string='FETCH_CONTACT_SUCCESS';
export const FETCH_CONTACT_FAILURE:string='FETCH_CONTACT_FAILURE';

export const SELECT_CONTACT_INFO:string ='SELECT_CONTACT_INFO';


export const fetchContacts = () =>{
    return async (dispatch) =>{
        try{
            dispatch({type: FETCH_CONTACT_REQUEST});
            let dataURL : string = 'https://gist.githubusercontent.com/thenaveensaggam/270f3990f36d1c1ad71cab4f06c8e67b/raw/77e0eb4c3d2cbafe88901006dac4e8daf2705f82/contacts.04022021.json';
            let response= await axois.get(dataURL);
            dispatch({type: FETCH_CONTACT_SUCCESS,payload:response.data});
        }
        catch (error) {
            dispatch({type: FETCH_CONTACT_FAILURE,payload:error.message});
        }
    };
};

export const selectedContactInfo = (concat:IContact) =>{
    return (dispatch) =>{
        dispatch({
            type:SELECT_CONTACT_INFO,
            payload:concat
        });
    };
};
