import React from 'react';
import {IContact} from "./models/IContact";
import axios from 'axios';

interface IProps {
    sendContact : (contact : IContact) => void
}

interface IState {
    contacts : IContact[]
}

class ContactList extends React.Component<IProps, IState>{

    constructor(props:IProps) {
        super(props);
        this.state = {
            contacts : [] as IContact[]
        }
    }

    componentDidMount(): void {
        let dataURL : string = 'https://gist.githubusercontent.com/mdnaveed64/50a3806064abcf5970dd74ff70d9de15/raw/b32ed0401ac50d9a25890a092850b5af8dd8f70b/contacts.08022021.json';
        axios.get(dataURL).then((response) => {
            this.setState({
                contacts : response.data
            });
        }).catch((error) => {
            console.error(error);
        });
    }

    render() {
        let {contacts} = this.state;
        return (
            <React.Fragment>
                <div className="input-group rounded">
                    <input type="search" className="form-control rounded" placeholder="Search" aria-label="Search"
                           aria-describedby="search-addon"/>
                    <span className="input-group-text border-0" id="search-addon">
                    <i className="fas fa-search"></i>
                 </span>
                </div>
               <pre>{JSON.stringify(this.state.contacts)}</pre>

                <table className="table table-hover text-center table-striped">
                    <thead className="bg-primary text-white">
                    <tr>
                        <th>SNO</th>
                        <th> FIRST NAME</th>
                        <th> LAST NAME</th>
                        <th>Email</th>
                    </tr>
                    </thead>
                    <tbody>
                    {
                        contacts.length > 0 &&
                        contacts.map(contact => {
                            return (
                                <tr key={contact.login.uuid} onClick={this.selectContact.bind(this, contact)}>
                                    <td>{contact.email}</td>

                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </React.Fragment>
        );
    }
}
export default ContactList;
