import * as productActions from './product-item.actions';

export interface IProduct {
    sno : string;
    name : string;
    image : string;
    price : number;
    qty : number;
}
export interface productState {
    product:IProduct
}

let initialState:productState ={
    product :{
        sno : 'AA102',
        name : 'Mi Watch',
        image : 'https://www.gizmochina.com/wp-content/uploads/2019/11/XIaomi-Mi-Watch-m-500x500.jpg',
        price : 1500,
        qty : 1
    }
};

export const reducer = (state=initialState,action:productActions.ActionType) =>{
    switch (action.type) {
        case productActions.INCR_PRODUCT_QTY:
            return{
                product :{
                    ...state.product,
                    qty : state.product.qty + 1
                }
            }
        case productActions.DECR_PRODUCT_QTY:
            return{
                product :{
                    ...state.product,
                    qty:(state.product.qty -1 >0) ? state.product.qty -1 :1
                }
            }
        default:return state;

    }
};
