import React from 'react';
import {Redirect, RouteComponentProps} from 'react-router-dom';
import {IProduct} from "./models/IProduct";
import axios from 'axios';

interface IProps extends RouteComponentProps<{productId : string}>{}

interface IState {
    selectedProduct : IProduct;
    isSubmitted : boolean;
}

class UpdateProduct extends React.Component<IProps, IState>{

    constructor(props:IProps) {
        super(props);
        this.state = {
            selectedProduct : {} as IProduct,
            isSubmitted : false
        }
    }

    componentDidMount(): void {
        let productId:string = this.props.match.params.productId;
        let dataURL: string = `http://127.0.0.1:5000/api/products/${productId}`;
        axios.get(dataURL).then((response) => {
            this.setState({
                selectedProduct : response.data
            });
        }).catch((error) => {
            console.error(error);
        });
    }

    updateInput = (event:React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        this.setState({
            selectedProduct : {
                ...this.state.selectedProduct,
                [event.target.name] : event.target.value
            }
        });
    };

    updateImage = async (event:React.ChangeEvent<HTMLInputElement | any>) => {
        let imageFile:Blob = event.target.files[0];
        let base64Image:string | ArrayBuffer = await this.convertBase64String(imageFile);
        this.setState({
            selectedProduct : {
                ...this.state.selectedProduct,
                image : base64Image.toString()
            }
        });
    };

    convertBase64String = (imageFile:Blob):Promise<string | ArrayBuffer> => {
        return new Promise((resolve, reject) => {
            let fileReader = new FileReader();
            fileReader.readAsDataURL(imageFile);
            fileReader.addEventListener('load', () => {
                if(fileReader.result){
                    resolve(fileReader.result);
                }
                else {
                    reject('Error Occurred');
                }
            })
        });
    };

    submitUpdateProduct = (event:React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        // submit form to server
        let dataURL : string = `http://127.0.0.1:5000/api/products/${this.state.selectedProduct._id}`;
        axios.put(dataURL, this.state.selectedProduct).then((response) => {
            this.setState({
                ...this.state,
                isSubmitted : true
            });
        }).catch((error) => {
            console.error(error);
        });
    };

    render() {
        let {selectedProduct} = this.state;
        return (
            <React.Fragment>
                {
                    this.state.isSubmitted ? <Redirect to="/products/admin"/> :
                        <React.Fragment>
                            <section className="mt-3">
                                <div className="container">
                                    <div className="row">
                                        <div className="col">
                                            <p className="h3 text-secondary">Update Product</p>
                                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ex impedit, incidunt ipsum nulla sapiente sint suscipit? A animi, error et fuga ipsum minus, nam officia praesentium quisquam, recusandae soluta voluptate?</p>
                                        </div>
                                    </div>
                                </div>
                            </section>
                            <section>
                                <div className="container">
                                    <div className="row">
                                        <div className="col-md-4">
                                            <div className="card">
                                                <div className="card-header bg-secondary text-white">
                                                    <p className="h4">Update Product</p>
                                                </div>
                                                <div className="card-body rgba-green-light">
                                                    <form onSubmit={this.submitUpdateProduct}>
                                                        <div className="form-group">
                                                            <input
                                                                required
                                                                name="name"
                                                                onChange={this.updateInput}
                                                                value={selectedProduct.name}
                                                                type="text" className="form-control" placeholder="Name"/>
                                                        </div>
                                                        <div className="form-group mt-2">
                                                            <input
                                                                name="image"
                                                                onChange={this.updateImage}
                                                                className="form-control" type="file" id="formFile"/>
                                                                <img src={selectedProduct.image} alt="" width="25" height="25"/>
                                                        </div>
                                                        <div className="form-group mt-2">
                                                            <input
                                                                required
                                                                name="price"
                                                                onChange={this.updateInput}
                                                                value={selectedProduct.price}
                                                                type="number" className="form-control" placeholder="Price"/>
                                                        </div>
                                                        <div className="form-group mt-2">
                                                            <input
                                                                required
                                                                name="qty"
                                                                onChange={this.updateInput}
                                                                value={selectedProduct.qty}
                                                                type="number" className="form-control" placeholder="Qty"/>
                                                        </div>
                                                        <div className="form-group mt-2">
                                                <textarea
                                                    required
                                                    name="info"
                                                    onChange={this.updateInput}
                                                    value={selectedProduct.info}
                                                    rows={3} className="form-control" placeholder="Information"/>
                                                        </div>
                                                        <div className="form-group mt-2">
                                                            <input type="submit" className="btn btn-secondary btn-sm" value="Update"/>
                                                        </div>
                                                    </form>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </section>
                        </React.Fragment>
                }
            </React.Fragment>
        );
    }
}
export default UpdateProduct;
