import React from "react";
interface IProps {}
interface IState {

}

let SMSApp:React.FC<IProps> =() =>{
    return(
        <React.Fragment>
            {/* <pre>{JSON.stringify(this.state)}</pre>*/}
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-6">
                            <div className="card">
                                <div className="card-header bg-success text-white">
                                    <h3>SMS App</h3>
                                </div>
                                <div className="card-body">
                                    <form>
                                            <textarea
                                                value={this.state.text}
                                                onChange={this.updateText}
                                                maxLength={this.state.maxCount}
                                                rows={4} className="form-control" placeholder="Your Text Here"/>
                                    </form>
                                </div>
                                <div className="card-footer">
                                    <p className="h3">The Characters Remaining :
                                        <span className="font-weight-bold">{this.state.charCount}</span>
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default SMSApp;
