import React, {useEffect} from "react";
import * as contactActions from '../redux/contact-app/contact-app.actions';
import * as contactReducer from '../redux/contact-app/contact-app.reducer';
import {useDispatch,useSelector} from "react-redux";
import Spinner from "./layout/Spinner";
import {IContact} from "./models/IContact";

interface IProps {}
interface IState {

}

let ContactList:React.FC<IProps> =() =>{
    let dispatch=useDispatch();

    let contactState:contactReducer.ContactState=useSelector((state:{contact:contactReducer.ContactState})=>{
        return state.contact;
    });

let{contacts,loading,errorMessage}=contactState;

    useEffect(()=>{
        dispatch(contactActions.fetchContacts());
    },[]);

    let selectContact=(contact:IContact):void =>{
      dispatch(contactActions.selectedContactInfo(contact));
    };

    return(
        <React.Fragment>
            {
                loading ? <Spinner/> :
                    <React.Fragment>
                        <React.Fragment>
                            <table className="table table-hover text-center table-striped">
                                <thead className="bg-primary text-white">
                                <tr>
                                    <th>SNO</th>
                                    <th>NAME</th>
                                    <th>Email</th>
                                    <th>Phone</th>
                                    <th>Location</th>
                                </tr>
                                </thead>
                                <tbody>
                                {
                                    contacts.length > 0 &&
                                    contacts.map(contact => {
                                        return (
                                            <tr key={contact.login.uuid}onClick={selectContact.bind(this, contact)} >
                                                <td>{contact.login.uuid.substr(contact.login.uuid.length - 5)}</td>
                                                <td>{contact.name.first} {contact.name.last}</td>
                                                <td>{contact.email}</td>
                                                <td>{contact.phone}</td>
                                                <td>{contact.location.city}</td>
                                            </tr>
                                        )
                                    })
                                }
                                </tbody>
                            </table>
                        </React.Fragment>
                    </React.Fragment>
            }

        </React.Fragment>
    )
};
export default ContactList;
