import React, {useState} from "react";
import {IContact} from "./models/IContact";
import ContactList from "./ContactList";
import ContactCard from "./ContactCard";
interface IProps {}
interface IState {
    selectedContact : IContact
}

let ContactApp:React.FC<IProps> =() =>{
    let [appState,setAppState] = useState<IState>({
        selectedContact : {} as IContact
    });
   let receiveContact = (contact : IContact) => {
        setAppState({
            selectedContact : contact
        })
    };

    return(
        <React.Fragment>

            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-primary">Contact App</p>
                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. A aspernatur aut commodi cumque ex illo magni natus nesciunt nobis odio officia officiis reiciendis rerum sapiente suscipit tenetur, ut velit voluptate.</p>
                        </div>
                    </div>
                </div>
            </section>
            <section>
                <div className="container">
                    <div className="row">
                        <div className="col-md-9">
                            <ContactList sendContact={receiveContact}/>
                        </div>
                        <div className="col-md-3">
                           {/* {
                                Object.keys(contactState.selectedContact).length > 0 &&
                                <ContactCard selectedContact={contactState.selectedContact}/>
                            }*/}
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default ContactApp;
