import React from 'react';
import {Link, Redirect} from "react-router-dom";
import {IProduct} from "./models/IProduct";
import axios from 'axios';

interface IProps {}

interface IState {
    product : IProduct;
    isSubmitted : boolean;
}

class CreateProduct extends React.Component<IProps, IState>{

    constructor(props:IProps) {
        super(props);
        this.state = {
            product : {} as IProduct,
            isSubmitted : false
        }
    }

    updateInput = (event:React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        this.setState({
            product : {
                ...this.state.product,
                [event.target.name] : event.target.value
            }
        });
    };

    updateImage = async (event:React.ChangeEvent<HTMLInputElement | any>) => {
        let imageFile:Blob = event.target.files[0];
        let base64Image:string | ArrayBuffer = await this.convertBase64String(imageFile);
        this.setState({
            product : {
                ...this.state.product,
                image : base64Image.toString()
            }
        });
    };

    convertBase64String = (imageFile:Blob):Promise<string | ArrayBuffer> => {
        return new Promise((resolve, reject) => {
            let fileReader = new FileReader();
            fileReader.readAsDataURL(imageFile);
            fileReader.addEventListener('load', () => {
                if(fileReader.result){
                    resolve(fileReader.result);
                }
                else {
                    reject('Error Occurred');
                }
            })
        });
    };

    submitCreateProduct = (event:React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        // submit form to server
        let dataURL : string = 'http://127.0.0.1:5000/api/products';
        axios.post(dataURL, this.state.product).then((response) => {
            this.setState({
                ...this.state,
                isSubmitted : true
            });
        }).catch((error) => {
            console.error(error);
        });
    };

    render() {
        let {product} = this.state;
        return (
            <React.Fragment>
                {
                    this.state.isSubmitted ? <Redirect to="/products/admin"/> :
                    <React.Fragment>
                        <section className="mt-3">
                            <div className="container">
                                <div className="row">
                                    <div className="col">
                                        <p className="h3 text-success">Create Product</p>
                                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ex impedit, incidunt ipsum nulla sapiente sint suscipit? A animi, error et fuga ipsum minus, nam officia praesentium quisquam, recusandae soluta voluptate?</p>
                                    </div>
                                </div>
                            </div>
                        </section>
                        <section>
                            <div className="container">
                                <div className="row">
                                    <div className="col-md-4">
                                        <div className="card">
                                            <div className="card-header bg-success text-white">
                                                <p className="h4">Create Product</p>
                                            </div>
                                            <div className="card-body rgba-green-light">
                                                <form onSubmit={this.submitCreateProduct}>
                                                    <div className="form-group">
                                                        <input
                                                            required
                                                            name="name"
                                                            onChange={this.updateInput}
                                                            value={product.name}
                                                            type="text" className="form-control" placeholder="Name"/>
                                                    </div>
                                                    <div className="form-group mt-2">
                                                        <input
                                                            required
                                                            name="image"
                                                            onChange={this.updateImage}
                                                            className="form-control" type="file" id="formFile"/>
                                                    </div>
                                                    <div className="form-group mt-2">
                                                        <input
                                                            required
                                                            name="price"
                                                            onChange={this.updateInput}
                                                            value={product.price}
                                                            type="number" className="form-control" placeholder="Price"/>
                                                    </div>
                                                    <div className="form-group mt-2">
                                                        <input
                                                            required
                                                            name="qty"
                                                            onChange={this.updateInput}
                                                            value={product.qty}
                                                            type="number" className="form-control" placeholder="Qty"/>
                                                    </div>
                                                    <div className="form-group mt-2">
                                                <textarea
                                                    required
                                                    name="info"
                                                    onChange={this.updateInput}
                                                    value={product.info}
                                                    rows={3} className="form-control" placeholder="Information"/>
                                                    </div>
                                                    <div className="form-group mt-2">
                                                        <input type="submit" className="btn btn-success btn-sm" value="Create"/>
                                                    </div>
                                                </form>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </section>
                    </React.Fragment>
                }
            </React.Fragment>
        );
    }
}
export default CreateProduct;
