import React, {useState} from "react";
import {GithubCred} from "./credentials/GithubCred";
import axios from 'axios';
import {IProfile} from "./models/IProfile";
import {IRepository} from "./models/IRepository";
import GithubProfile from "./profile/GithubProfile";
interface IProps {}


interface IState {
    githubUserName : string,
    profile : IProfile,
    repos : IRepository[]
}

let GithubProfileSearchApp:React.FC<IProps> =() =>{
    let[githubState,setGithubState] = useState<IState>({
        githubUserName : '',
        profile : {} as IProfile,
        repos : [] as IRepository[]

    });
  let  changeInput = (event : React.ChangeEvent<HTMLInputElement>) => {
       setGithubState({
            ...githubState,
            githubUserName : event.target.value
        });
    };

    // search Profile of a user
   let searchProfile = (githubUser:string) => {
        let dataURL : string = `https://api.github.com/users/${githubUser}?Client_ID=${GithubCred.Client_ID}&Client_Secret=${GithubCred.Client_Secret}`;
        axios.get(dataURL).then((response) => {
           setGithubState({
                ...githubState,
                profile : response.data
            });
        }).catch((error) => {
            console.error(error);
        });
    };


    // search Repos of a user
    let searchRepos = (githubUser:string) => {
        let dataURL : string = `https://api.github.com/users/${githubUser}/repos?Client_ID=${GithubCred.Client_ID}&Client_Secret=${GithubCred.Client_Secret}`;
        axios.get(dataURL).then((response) => {
           setGithubState({
                ...githubState,
                repos : response.data
            });
        }).catch((error) => {
            console.error(error);
        });
    };


    let submitSearch = (event : React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        searchProfile(githubState.githubUserName);
        searchRepos(githubState.githubUserName);
    };

    return(
        <React.Fragment>
            {/*<pre>{JSON.stringify(githubState.profile)}</pre>
            <pre>{JSON.stringify(githubState.repos)}</pre>*/}
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-primary">Github Search App</p>
                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Doloremque dolorum minus obcaecati odio porro quae vero voluptas voluptatem. Dignissimos eum ex fugit non, quidem totam. Dignissimos molestiae pariatur sit tenetur.</p>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-md-4">
                            <form onSubmit={submitSearch}>
                                <div className="row no-gutters">
                                    <div className="col">
                                        <div className="form-group">
                                            <input
                                                value={githubState.githubUserName}
                                                onChange={changeInput}
                                                type="text" className="form-control" placeholder="Github Username"/>
                                        </div>
                                    </div>
                                    <div className="col-md-4">
                                        <div>
                                            <input type="submit" className="btn btn-primary btn-sm" value="Search"/>
                                        </div>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </section>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            {
                                Object.keys(githubState.profile).length > 0 &&
                                <GithubProfile profile={githubState.profile}/>
                            }
                        </div>
                    </div>
                </div>
            </section>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            {/*{
                                this.state.repos.length > 0 &&
                                <GithubRepos repos={this.state.repos}/>
                            }*/}
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default GithubProfileSearchApp;
