import React, {useRef} from "react";
import card1  from '../../assets/img/card_1.jpg';
interface IProps {}
interface IState {

}

let ImageSelect:React.FC<IProps> =() =>{
    let imageEl = useRef(null);

    let clickButton =() =>{
        alert(imageEl.current)
    };

    return(
        <React.Fragment>
           <section className="mt-3">
               <div className="container">
                   <div className="row">
                       <div className="col-md-4">
                           <div className="card">
                               <div className="card-header">
                                   <img ref={imageEl} src={card1} alt="" className="img-fluid"/>
                               </div>
                               <div className="card-body">
                                   <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Architecto culpa cumque esse est in incidunt itaque minus modi nesciunt provident, quas quo repellendus saepe voluptatem voluptates. Doloribus nulla obcaecati velit!</p>
                                   <button onClick={clickButton} className="btn btn-success btn-sm">click</button>
                               </div>
                           </div>
                       </div>
                   </div>
               </div>
           </section>

        </React.Fragment>
    )
};
export default ImageSelect;
