//Actions

export const GOOD_MORNING:string ='GOOD_MORNING';
export const GOOD_AFTERNOON:string ='GOOD_AFTERNOON';
export const GOOD_EVENING:string ='GOOD_EVENING';

//Action Type
export interface ActionType {
    type:string
}

//Action Creator
export const sayGoodMorning =():ActionType =>{
    return{
        type:GOOD_MORNING
    }
};
export const sayAfternoon =():ActionType =>{
    return{
        type:GOOD_AFTERNOON
    }
};
export const sayGoodEvening =():ActionType =>{
    return{
        type:GOOD_EVENING
    }

};
