import React, {useEffect} from "react";
import * as userActions from '../redux/user/user.actions';
import * as userReducer from '../redux/user/user.reducer';
import {useDispatch,useSelector} from "react-redux";
import Spinner from "./layout/Spinner";

interface IProps {}


let UserList:React.FC<IProps> =() =>{
    let dispatch=useDispatch();
//get user data from Redux store
   let userState:userReducer.UserState=useSelector((state:{user:userReducer.UserState})=>{
       return state.user;
   });

//dispatch an action to fetch user data from the server
    useEffect(() =>{

    },[]);
     let clickUsers =() =>{
         dispatch(userActions.fetchUser());
     };

    let{users,loading,errorMessage} =userState;
    return(
        <React.Fragment>
            <button onClick={clickUsers} className="btn btn-success btn-sm">Get Users </button>
            {
                loading ? <Spinner/> :
                    <React.Fragment>
                        <section className="mt-3">
                            <div className="container">
                                <div className="row">
                                    <div className="col">
                                        <table className="table table-hover text-center table-primary table-striped">
                                            <thead>
                                            <tr>
                                                <th>SNO</th>
                                                <th>NAME</th>
                                                <th>Email</th>
                                                <th>Phone</th>
                                                <th>Website</th>
                                                <th>Location</th>
                                            </tr>
                                            </thead>
                                            <tbody>
                                            {
                                                users.length > 0 &&
                                                users.map(user => {
                                                    return (
                                                        <tr key={user.id}>
                                                            <td>{user.id}</td>
                                                            <td>{user.name}</td>
                                                            <td>{user.email}</td>
                                                            <td>{user.phone}</td>
                                                            <td>{user.website}</td>
                                                            <td>{user.address.city}</td>
                                                        </tr>
                                                    )
                                                })
                                            }
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </section>
                        </React.Fragment>
                        }
        </React.Fragment>
    )
};
export default UserList;
