import React, {useState} from "react";
import ChildCard from "./ChildCard";
interface IProps {}
interface IState {
    pText : string;
    cText : string;
}

let ParentCard:React.FC<IProps> =() =>{
    let [parentState,setParentState] =useState<IState>({
        pText : '',
        cText : ''
    });
   let updateInput = (event : React.ChangeEvent<HTMLInputElement>) => {
        setParentState({
            ...parentState,
            pText : event.target.value
        });
    };
   let receiveData = (value : string):void => {
        setParentState({
            ...parentState,
            cText : value
        });
    };


    return(
        <React.Fragment>
            <section className="mt-3">
                <div className="container">
                    <div className="row">
                        <div className="col-md-8">
                            <div className="card">
                                <div className="card-body rgba-blue-light text-white">
                                    <div className="row">
                                        <div className="col-md-4">
                                            <form>
                                                <div className="form-group">
                                                    <input
                                                        value={parentState.pText}
                                                        onChange={updateInput}
                                                        type="text" className="form-control" placeholder="Parent Text"/>
                                                </div>
                                            </form>
                                        </div>
                                    </div>
                                    <small className="text-dark font-weight-bold">{parentState.cText}</small>
                                    <ChildCard pText={parentState.pText} sendData={receiveData}/>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
};
export default ParentCard;
