import * as cartActions from './shopping-cart.actions';

export interface IProduct {
    sno : string;
    name : string;
    image : string;
    price : number;
    qty : number;
}

export interface CartState {
    products:IProduct[]
}

let initialState : CartState = {
    products : [
        {
            sno : 'AA102',
            name : 'Mi Watch',
            image : 'https://www.gizmochina.com/wp-content/uploads/2019/11/XIaomi-Mi-Watch-m-500x500.jpg',
            price : 1500,
            qty : 2
        },
        {
            sno : 'AA103',
            name : 'Apple Watch',
            image : 'https://images-na.ssl-images-amazon.com/images/I/61OUIIXnPqL._AC_SX569_.jpg',
            price : 1800,
            qty : 2
        },
        {
            sno : 'AA104',
            name : 'Sumsung Watch',
            image : 'https://images-na.ssl-images-amazon.com/images/I/419QC1uNzSL._SY355_.jpg',
            price : 1950,
            qty : 2
        },
    ]
}

export const reducer = (state= initialState , action:cartActions.ActionType) =>{
    switch (action.type) {
        case cartActions.INCR_PRODUCT_QTY:
            let incrProducts:IProduct[] =state.products.map(product => {
                if(product.sno === action.payload.sno){
                    return {
                        ...product,
                        qty : product.qty + 1
                    }
                }
                return product;
            });
            return{
               products : [...incrProducts]
            };
        case cartActions.DECR_PRODUCT_QTY:
            let decrProducts:IProduct[] =state.products.map(product => {
                if(product.sno === action.payload.sno){
                    return {
                        ...product,
                        qty : product.qty - 1 > 0 ? product.qty - 1 : 1
                    }
                }
                return product;
            });
            return{
                products : [...decrProducts]
            };
        default:return state;
    }
};
